    public void test() {


        try {


            Socket socket = new Socket("192.168.1.199", 17000);

            socket.setKeepAlive(true);

            File file = new File("/Users/frank/input2.txt");

            FileInputStream fileInputStream = new FileInputStream(file);

            //由系统标准输入设备构造BufferedReader对象

            OutputStream os = socket.getOutputStream();

            //由Socket对象得到输出流，并构造PrintWriter对象

            BufferedReader is = new BufferedReader(new InputStreamReader(socket.getInputStream()));

            //由Socket对象得到输入流，并构造相应的BufferedReader对象

            byte[] content = new byte[128];

            fileInputStream.read(content);

            byte[] line = "\r\n".getBytes();

            System.arraycopy(line, 0, content, 118, line.length);

            os.write(content);

            os.flush();

            System.in.read(new byte[]{});

            os.close();

            is.close(); //关闭Socket输入流

            socket.close(); //关闭Socket

            System.out.printf("end......");
        } catch (Exception e) {
            System.out.printf("err......");
            e.printStackTrace();

        }

    }


    public static byte[] buildCommonHead(int second) {

        int curTime = BoostrapTest.baseSecond + second;

        byte t1 = (byte) ((curTime >> 24) & 0xFF);
        byte t2 = (byte) ((curTime >> 16) & 0xFF);
        byte t3 = (byte) ((curTime >> 8) & 0xFF);
        byte t4 = (byte) ((curTime) & 0xFF);
        byte[] commonData = new byte[]{
                (byte) 0xFE,
                0x00, 0x76,
                0x01,
                (byte) 0xD1, (byte) 0xEA,
                t1, t2, t3, t4,
                0x11, 0x22, 0x33, 0x44, 0x55, 0x66};
        return commonData;
    }

    public void testOpticData() {


        byte[] allDatas = getData();

        try {

            Socket socket = new Socket("114.215.181.106", 17000);

            socket.setKeepAlive(true);


            //由系统标准输入设备构造BufferedReader对象

            OutputStream os = socket.getOutputStream();


            byte[] onesecond;
            for (int i = 0; i < 60; ++i) {
                onesecond = new byte[100];
                System.arraycopy(allDatas, i * 60, onesecond, 0, 100);

//                printByteArray(onesecond);

                byte[] commondata = buildCommonHead(i);

                byte[] finaldata = new byte[commondata.length + onesecond.length + 2];

                System.arraycopy(
                        commondata, 0, finaldata, 0, commondata.length
                );

                System.arraycopy(
                        onesecond, 0, finaldata, commondata.length, onesecond.length
                );

                finaldata[commondata.length + onesecond.length] = (byte) 0xFF;
                finaldata[commondata.length + onesecond.length + 1] = (byte) 0xFF;

                printByteArray(finaldata);


                os.write(finaldata);
                os.flush();
                Thread.sleep(1 * 1000);// sleep five seconds;
            }

            os.close();

            socket.close(); //关闭Socket

            System.out.printf("end......");
        } catch (Exception e) {
            System.out.printf("err......");
            e.printStackTrace();

        }


    }


    public static void main(String[] args) {
        BoostrapTest test = new BoostrapTest();
        System.out.println("args = [" + ByteUtils.hexToString(test.getData()) + "]");
    }

