    private static void printByteArray(byte[] array) {
        StringBuilder sb = new StringBuilder();

        for (int i = 0; i < array.length; ) {
            sb.append(((int) array[i]&0xFF << 8) + ((int) array[i + 1]&0xFF)).append(",");
            i += 2;
        }

        System.out.println("array's length:" + array.length + ",array = [" + sb + "]");
    }

